        uint64 indexed _datasetId,
        address indexed _provider
    );

    /// @notice Event emitted when replica requirement is submitted for a new dataset.
    event DatasetReplicaRequirementSubmitted(
        uint64 indexed _datasetId,
        address indexed _provider
